import { PluginSimple } from 'markdown-it';
import { Plugin } from 'vuepress/core';

type RevealJsTheme = 'auto' | 'beige' | 'black' | 'blood' | 'league' | 'moon' | 'night' | 'serif' | 'simple' | 'sky' | 'solarized' | 'white';

type RevealJsPlugin = 'highlight' | 'math' | 'notes' | 'search' | 'zoom';
/**
 * reveal.js plugin options
 */
interface RevealJsPluginOptions {
    /**
     * Reveal.js plugins
     *
     * 幻灯片插件
     *
     * @default []
     */
    plugins?: RevealJsPlugin[];
    /**
     * Reveal.js themes
     *
     * 幻灯片主题
     *
     * @default ["auto"]
     */
    themes?: RevealJsTheme[];
    /**
     * Slide layout name
     *
     * 幻灯片布局名称
     *
     * @default 'SlidePage'
     */
    layout?: string | false;
}

declare const revealJs: PluginSimple;

declare const revealJsPlugin: ({ plugins, themes, layout, }?: RevealJsPluginOptions) => Plugin;

export { revealJs, revealJsPlugin };
export type { RevealJsPlugin, RevealJsPluginOptions };
